import java.util.*;

public class Foo {

    enum E {A, B, C}

    public static boolean bar() {
        Set<E> set = new HashSet<>();
        return set.contains(E.A);
    }
    public static boolean bar2() {
        Set<E> set = new TreeSet<>();
        return set.contains(E.A);
    }
}

import java.util.*;

public class Foo {
    public enum E {A, B, C}

    public static Map<E, Boolean> bar() {
        return new HashMap<>();
    }
    public static Map<E, Boolean> bar() {
        return new TreeMap<>();
    }
}

import java.util.*;

public class Foo {
    public enum E {A, B, C}

    public static Map<E, Boolean> bar() {
        Set<E> set = new LinkedHashSet<>();
        return new LinkedHashMap<>();
    }
